<style module lang="less">
  .crosshairGallery{
    position: absolute;
    left: 5px;
    top: 5px;
    text-align: left;
    color: white;
    min-width: 230px;
    min-height: 330px;
    background-color: rgba(0,0,0,0.3);
    border-radius: 5px;
    overflow-y: auto;
    overflow-x: hidden;
  }
  .crosshairs{
    position: absolute;
    width: 100%;
    height: 100%;
    display: grid;
    grid-column-gap: 0px;
    grid-row-gap: 0px;
    grid-template-columns: 100px 100px;
    justify-content: space-evenly;
    align-items: start;
  }
  .crosshair{
    box-sizing: border-box;
    position: relative;
    width: 102px;
    height: 102px;
    border: 1px solid rgba(0,0,0,0.4);
    background-color: rgba(0,0,0,0.2);
    margin-bottom: 2px;
    margin-top: 2px;
  }
  .crosshair:hover{
    cursor: pointer;
  }

  .cScroll{
    overflow-y: auto;
    flex-grow: 1;
  }
  .cScroll::-webkit-scrollbar {
    width: 10px;
  }

  .cScroll::-webkit-scrollbar-track {
    background: #999;
    border-radius: 0px 0px 5px 0px;
  }

  .cScroll::-webkit-scrollbar-track:hover {
    background: #888;
  }

  .cScroll::-webkit-scrollbar-thumb {
    background: #555;
    border-radius: 1px 1px 4px 1px;
    box-sizing: border-box;
  }

  .cScroll::-webkit-scrollbar-thumb:hover {
    background: #333;
  }
</style>
